// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract VotingContract {
    // Define the voting period duration (in seconds)
    uint public votingPeriodDuration;

    // Define the poll question and options
    string public pollQuestion;
    string public optionYes;
    string public optionNo;

    // Define the voting status (true = voting period active, false = voting period ended)
    bool public votingActive;

    // Define the vote counts for each option
    uint public yesVotes;
    uint public noVotes;

    // Define the mapping of voters to their votes
    mapping (address => bool) public voterVotes;

    // Event emitted when a vote is cast
    event VoteCast(address voter, bool vote);

    // Event emitted when the voting period ends
    event VotingPeriodEnded();

    // Define the owner of the contract
    address public owner;

    // Constructor function
    constructor() public {
        owner = msg.sender;
        votingActive = true;
    }

    // Function to set the voting period duration
    function setVotingPeriodDuration(uint _duration) public {
        require(msg.sender == owner, "Only the owner can set the voting period duration");
        votingPeriodDuration = _duration;
    }

    // Function to display the poll
    function getPoll() public view returns (string memory, string memory, string memory) {
        return (pollQuestion, optionYes, optionNo);
    }

    // Function to cast a vote
    function castVote(bool _vote) public {
        require(votingActive, "Voting period has ended");
        require(!voterVotes[msg.sender], "You have already voted");

        if (_vote) {
            yesVotes++;
        } else {
            noVotes++;
        }

        voterVotes[msg.sender] = true;

        emit VoteCast(msg.sender, _vote);
    }

    // Function to check if the voting period has ended
    function hasVotingPeriodEnded() public view returns (bool) {
        return block.timestamp > votingPeriodDuration;
    }

    // Function to tally the results
    function getResults() public view returns (uint, uint) {
        require(hasVotingPeriodEnded(), "Voting period has not ended");

        return (yesVotes, noVotes);
    }

    // Function to end the voting period
    function endVotingPeriod() public {
        require(msg.sender == owner, "Only the owner can end the voting period");
        votingActive = false;
        emit VotingPeriodEnded();
    }
}
